import _xyz_map from './_xyz_map.mjs';

export default map_id => {

    _xyz_map.ctrl = _xyz_map.L.map(map_id, {
        scrollWheelZoom: false,
        zoomControl: false
    }).setView([52, 0], 7);

    _xyz_map.ctrl.addLayer(_xyz_map.L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png'));

}